# --------------------------------------------------------------- Imports ---------------------------------------------------------------- #

# System
from typing import List

# Local
from .base_list import BaseList
from .base_user import BaseUser
from .imaged_title import ImagedTitle

# ---------------------------------------------------------------------------------------------------------------------------------------- #



# ----------------------------------------------------------- class: TitleList ----------------------------------------------------------- #

class TitleList(BaseList):

    # ------------------------------------------------------------- Init ------------------------------------------------------------- #

    def __init__(
        self,
        id: str,
        name: str,
        owner: BaseUser,
        titles: List[ImagedTitle]
    ):
        super().__init__(id, name)

        self.owner = owner
        self.titles = titles


