class Config:
  # Name of the Makefile generated by STM32CubeMX
  standard_makefile = 'Makefile'

  # Name of C++ Makefile genereted by mkstm32
  cpp_makefile = 'Makefile_cpp.mk'

  # Name of directory containing compilation results
  build_dir = 'build'

  # Entry point of an STM32 program
  flash_address = '0x8000000'

