if __name__ == "__main__":
    from plotly.colors import PLOTLY_SCALES, hex_to_rgb, label_rgb

    all_scales = {}
    for scale_name, colorscale in PLOTLY_SCALES.copy().items():
        if colorscale[0][1].startswith("#"):
            colorscale = [[s, label_rgb(hex_to_rgb(c))] for s, c in colorscale]
        all_scales[scale_name.lower()] = colorscale
    print(all_scales)
