# Cell

from multiprocessing import Pool

# Cell

def parallel_run(func,args,thread=10):
    pool = Pool(thread)
    if isinstance(args[0],list):
        results = pool.starmap(func,args)
    else:
        results = pool.map(func,args)
    pool.close()
    pool.join()
    return results
